import { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "../components/Navbar";

const Admin = () => {
  const [pedidos, setPedidos] = useState([]);
  const [notificaciones, setNotificaciones] = useState([]);

  useEffect(() => {
    const fetchPedidos = async () => {
      const res = await axios.get("http://localhost:8000/pedidos/");
      setPedidos(res.data);
    };

    fetchPedidos();

    const ws = new WebSocket("ws://localhost:8000/ws/admin");

    ws.onmessage = (event) => {
      setNotificaciones((prev) => [...prev, event.data]);
    };

    return () => ws.close();
  }, []);

  return (
    <div>
      <Navbar />
      <div className="p-5">
        <h1 className="text-3xl font-bold mb-4">Panel de AdministraciÃ³n</h1>

        <h2 className="text-xl mt-4">ðŸ”” Notificaciones</h2>
        <ul>
          {notificaciones.map((msg, index) => (
            <li key={index} className="border p-2 my-2">{msg}</li>
          ))}
        </ul>

        <h2 className="text-xl mt-4">ðŸ“Œ Pedidos</h2>
        <ul>
          {pedidos.map((pedido) => (
            <li key={pedido.id} className="border p-2 my-2">
              Pedido en Mesa {pedido.mesa_id} - Items: {pedido.items.join(", ")}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Admin;
