import { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "../components/Navbar";

const Admin = () => {
  const [pedidos, setPedidos] = useState([]);
  const [notificaciones, setNotificaciones] = useState([]);
  const [productos, setProductos] = useState([]);
  const [nuevoProducto, setNuevoProducto] = useState({
    nombre: "",
    categoria: "",
    precio: 0.0
  });

  useEffect(() => {
    const fetchPedidos = async () => {
      try {
        const res = await axios.get("http://localhost:8000/pedidos/");
        setPedidos(res.data);
      } catch (error) {
        console.error("Error al obtener pedidos:", error);
      }
    };

    const fetchProductos = async () => {
      try {
        const res = await axios.get("http://localhost:8000/productos/");
        setProductos(res.data);
      } catch (error) {
        console.error("Error al obtener productos:", error);
      }
    };

    fetchPedidos();
    fetchProductos();

    const ws = new WebSocket("ws://localhost:8000/ws/admin");

    ws.onmessage = (event) => {
      setNotificaciones((prev) => [...prev, event.data]);
    };

    return () => ws.close();
  }, []);

  const handleAgregarProducto = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:8000/productos/", nuevoProducto);
      setProductos([...productos, res.data]);
      setNuevoProducto({ nombre: "", categoria: "", precio: 0.0 });
    } catch (error) {
      console.error("Error al agregar producto:", error);
    }
  };

  return (
    <div>
      <Navbar />
      <div className="p-5">
        <h1 className="text-3xl font-bold mb-4">Panel de Administraci√≥n</h1>

        <h2 className="text-xl mt-4">üîî Notificaciones</h2>
        <ul>
          {notificaciones.map((msg, index) => (
            <li key={index} className="border p-2 my-2">{msg}</li>
          ))}
        </ul>

        <h2 className="text-xl mt-4">üìå Pedidos</h2>
        <ul>
          {pedidos.map((pedido) => (
            <li key={pedido.id} className="border p-2 my-2">
              Pedido en Mesa {pedido.mesa_id} - Items: {pedido.items} - Total: {pedido.total}
            </li>
          ))}
        </ul>

        <h2 className="text-xl mt-4">üçî Productos</h2>
        <ul>
          {productos.map((producto) => (
            <li key={producto.id} className="border p-2 my-2">
              {producto.nombre} - {producto.categoria} - ${producto.precio.toFixed(2)}
            </li>
          ))}
        </ul>

        <h2 className="text-xl mt-4">‚ûï Agregar Producto</h2>
        <form onSubmit={handleAgregarProducto} className="flex flex-col space-y-4">
          <input
            type="text"
            placeholder="Nombre del Producto"
            value={nuevoProducto.nombre}
            onChange={(e) => setNuevoProducto({ ...nuevoProducto, nombre: e.target.value })}
            className="border p-2"
            required
          />
          <select
            value={nuevoProducto.categoria}
            onChange={(e) => setNuevoProducto({ ...nuevoProducto, categoria: e.target.value })}
            className="border p-2"
            required
          >
            <option value="">Selecciona una categor√≠a</option>
            <option value="bebida">Bebida</option>
            <option value="calzone">Calzone</option>
            <option value="pizza">Pizza</option>
            <option value="entrada">Entrada</option>
            <option value="postre">Postre</option>
          </select>
          <input
            type="number"
            step="0.01"
            placeholder="Precio"
            value={nuevoProducto.precio}
            onChange={(e) => setNuevoProducto({ ...nuevoProducto, precio: parseFloat(e.target.value) })}
            className="border p-2"
            required
          />
          <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
            Agregar Producto
          </button>
        </form>
      </div>
    </div>
  );
};

export default Admin;
