import { useState } from "react";
import axios from "axios";
import Navbar from "../components/Navbar";

const Mesas = () => {
  const [selectedMesa, setSelectedMesa] = useState(null);
  const [pedido, setPedido] = useState({
    mesa_id: null,
    items: []
  });

  const mesas = [1, 2, 3, 4, 5]; // Números de las mesas

  const handleSeleccionarMesa = (mesa) => {
    setSelectedMesa(mesa);
    setPedido({ ...pedido, mesa_id: mesa });
  };

  const agregarItem = (item) => {
    setPedido({ ...pedido, items: [...pedido.items, item] });
  };

  const handleSubmitPedido = async (e) => {
    e.preventDefault();
    try {
      // Convertir los ítems a cadenas de texto
      const itemsAsStrings = pedido.items.map(item => `${item.tipo}: ${item.nombre}`);
      const response = await axios.post("http://localhost:8000/pedidos/", {
        mesa_id: pedido.mesa_id,
        items: itemsAsStrings
      });
      alert("Pedido realizado con éxito");
      setPedido({ mesa_id: null, items: [] });
      setSelectedMesa(null);
    } catch (error) {
      console.error("Error al realizar el pedido:", error);
      alert("Error al realizar el pedido");
    }
  };

  return (
    <div>
      <Navbar />
      <div className="p-5">
        <h1 className="text-3xl font-bold mb-4">Mesas Disponibles</h1>
        <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-5 gap-4">
          {mesas.map((mesa) => (
            <div
              key={mesa}
              className="border p-4 rounded shadow flex flex-col items-center bg-white"
            >
              <h2 className="text-xl font-semibold">Mesa {mesa}</h2>
              <button
                className="mt-2 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
                onClick={() => handleSeleccionarMesa(mesa)}
              >
                Seleccionar Mesa
              </button>
            </div>
          ))}
        </div>

        {selectedMesa && (
          <div className="mt-8 p-4 border rounded shadow bg-white">
            <h2 className="text-xl font-semibold mb-4">Agregar Pedido para Mesa {selectedMesa}</h2>
            <form onSubmit={handleSubmitPedido}>
              <div className="mb-4">
                <label className="block mb-2">Pizzas:</label>
                <select
                  className="border p-2 w-full"
                  onChange={(e) => agregarItem({ tipo: "pizza", nombre: e.target.value })}
                >
                  <option value="">Selecciona una pizza</option>
                  <option value="Margarita">Margarita</option>
                  <option value="4 Estaciones">4 Estaciones</option>
                  <option value="Abbruzzese">Abbruzzese</option>
                  <option value="La Bambina">La Bambina</option>
                  <option value="Vegetariana">Vegetariana</option>
                  <option value="De la Nona - Especial">De la Nona - Especial</option>
                  <option value="Americana">Americana</option>
                  <option value="Hawaiana">Hawaiana</option>
                  <option value="Combinación">Combinación</option>
                  <option value="Frutos del Mar">Frutos del Mar</option>
                </select>
              </div>

              <div className="mb-4">
                <label className="block mb-2">Calzones:</label>
                <select
                  className="border p-2 w-full"
                  onChange={(e) => agregarItem({ tipo: "calzone", nombre: e.target.value })}
                >
                  <option value="">Selecciona un calzone</option>
                  <option value="Clásico">Clásico</option>
                  <option value="Issa Calzone">Issa Calzone</option>
                </select>
              </div>

              <div className="mb-4">
                <label className="block mb-2">Entradas:</label>
                <select
                  className="border p-2 w-full"
                  onChange={(e) => agregarItem({ tipo: "entrada", nombre: e.target.value })}
                >
                  <option value="">Selecciona una entrada</option>
                  <option value="Bruschetta al Pomodoro">Bruschetta al Pomodoro</option>
                  <option value="Trio de Empanaditas Horneadas">Trio de Empanaditas Horneadas</option>
                  <option value="Ceviche de Corvina">Ceviche de Corvina</option>
                  <option value="Ceviche de Camarones">Ceviche de Camarones</option>
                  <option value="Dip de Pollo Gratinado de Mozzarella">Dip de Pollo Gratinado de Mozzarella</option>
                </select>
              </div>

              <div className="mb-4">
                <label className="block mb-2">Postres:</label>
                <select
                  className="border p-2 w-full"
                  onChange={(e) => agregarItem({ tipo: "postre", nombre: e.target.value })}
                >
                  <option value="">Selecciona un postre</option>
                  <option value="Flan de la Casa">Flan de la Casa</option>
                  <option value="Tiramisú">Tiramisú</option>
                </select>
              </div>

              <div className="mb-4">
                <label className="block mb-2">Bebidas:</label>
                <select
                  className="border p-2 w-full"
                  onChange={(e) => agregarItem({ tipo: "bebida", nombre: e.target.value })}
                >
                  <option value="">Selecciona una bebida</option>
                  <option value="Fresa">Fresa</option>
                  <option value="Melocotón Papaya">Melocotón Papaya</option>
                  <option value="Banana">Banana</option>
                  <option value="Piña">Piña</option>
                  <option value="Chocolate">Chocolate</option>
                  <option value="Té Frío">Té Frío</option>
                  <option value="Sodas de Lata Variadas">Sodas de Lata Variadas</option>
                </select>
              </div>

              <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600">
                Agregar Pedido
              </button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default Mesas;
