import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [nombre, setNombre] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      const res = await axios.post("http://localhost:8000/login/", { nombre, password });
      localStorage.setItem("token", res.data.access_token);
      navigate("/mesas");
    } catch (error) {
      alert("Credenciales incorrectas");
    }
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen">
      <h1 className="text-2xl">Iniciar Sesión</h1>
      <input className="border p-2 my-2" placeholder="Usuario" onChange={(e) => setNombre(e.target.value)} />
      <input className="border p-2 my-2" type="password" placeholder="Contraseña" onChange={(e) => setPassword(e.target.value)} />
      <button className="bg-blue-500 text-white px-4 py-2" onClick={handleLogin}>Entrar</button>
    </div>
  );
};

export default Login;
