import { useState, useEffect } from "react";
import axios from "axios";

const Mesas = () => {
  const [mesas, setMesas] = useState([]);

  useEffect(() => {
    const fetchMesas = async () => {
      const res = await axios.get("http://localhost:8000/mesas/", {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setMesas(res.data);
    };
    fetchMesas();
  }, []);

  const handlePedido = async (mesaId) => {
    try {
      const res = await axios.post(
        "http://localhost:8000/pedidos/",
        { mesa_id: mesaId, total: 0, estado: "pendiente" },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );
      alert("Pedido realizado con éxito");
      // Aquí puedes actualizar la lista de mesas si es necesario
    } catch (error) {
      console.error("Error al realizar el pedido:", error);
      alert("Error al realizar el pedido");
    }
  };

  return (
    <div className="p-5">
      <h1 className="text-2xl">Mesas</h1>
      <ul>
        {mesas.map((mesa) => (
          <li key={mesa.id} className="border p-2 my-2 flex justify-between items-center">
            <span>Mesa {mesa.numero} - {mesa.ocupada ? "Ocupada" : "Libre"}</span>
            {!mesa.ocupada && (
              <button
                className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
                onClick={() => handlePedido(mesa.id)}
              >
                Realizar Pedido
              </button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Mesas;
