import { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "../components/Navbar";
import { Bar } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from "chart.js";

// Configurar Chart.js
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const Admin = () => {
  const [pedidos, setPedidos] = useState([]);
  const [notificaciones, setNotificaciones] = useState([]);
  const [productos, setProductos] = useState([]);
  const [nuevoProducto, setNuevoProducto] = useState({ nombre: "", categoria: "", precio: "" });
  const [ventasDiarias, setVentasDiarias] = useState([]);

  useEffect(() => {
    const fetchPedidos = async () => {
      try {
        const res = await axios.get("http://localhost:8000/pedidos/");
        setPedidos(res.data);
      } catch (error) {
        console.error("Error al obtener pedidos:", error);
      }
    };

    const fetchProductos = async () => {
      try {
        const res = await axios.get("http://localhost:8000/productos/");
        setProductos(res.data);
      } catch (error) {
        console.error("Error al obtener productos:", error);
      }
    };

    const fetchVentas = async () => {
      try {
        const res = await axios.get("http://localhost:8000/ventas/");
        setVentasDiarias(res.data);
      } catch (error) {
        console.error("Error al obtener ventas:", error);
      }
    };

    fetchPedidos();
    fetchProductos();
    fetchVentas();

    const ws = new WebSocket("ws://localhost:8000/ws/admin");
    ws.onmessage = (event) => setNotificaciones((prev) => [...prev, event.data]);

    return () => ws.close();
  }, []);

  const handleAgregarProducto = async (e) => {
    e.preventDefault();
    try {
      const precio = parseFloat(nuevoProducto.precio);
      if (isNaN(precio)) {
        alert("Por favor, ingrese un precio válido.");
        return;
      }
      const res = await axios.post("http://localhost:8000/productos/", { ...nuevoProducto, precio });
      setProductos([...productos, res.data]);
      setNuevoProducto({ nombre: "", categoria: "", precio: "" });
    } catch (error) {
      console.error("Error al agregar producto:", error);
    }
  };

  // Datos de la gráfica de ventas
  const ventasData = {
    labels: ventasDiarias.map((venta) => venta.fecha),
    datasets: [
      {
        label: "Total de Ventas ($)",
        data: ventasDiarias.map((venta) => venta.total),
        backgroundColor: "rgba(54, 162, 235, 0.5)",
      },
    ],
  };

  return (
    <div className="bg-gray-100 min-h-screen">
      <Navbar />
      <div className="container mx-auto p-5">
        <h1 className="text-3xl font-bold mb-6 text-center">📊 Panel de Administración</h1>

        {/* 🔔 Notificaciones */}
        <div className="bg-white p-4 rounded shadow mb-6">
          <h2 className="text-xl font-bold mb-3">🔔 Notificaciones</h2>
          <ul>
            {notificaciones.map((msg, index) => (
              <li key={index} className="border p-2 my-2">{msg}</li>
            ))}
          </ul>
        </div>

        {/* 📊 Gráfico de Ventas */}
        <div className="bg-white p-4 rounded shadow mb-6">
          <h2 className="text-xl font-bold mb-3">📈 Ventas Diarias</h2>
          <Bar data={ventasData} />
        </div>

        {/* 📌 Pedidos */}
        <div className="bg-white p-4 rounded shadow mb-6">
          <h2 className="text-xl font-bold mb-3">📌 Pedidos</h2>
          <ul>
            {pedidos.map((pedido) => (
              <li key={pedido.id} className="border p-2 my-2">
                <span className="font-bold">Mesa {pedido.mesa_id}</span> - {pedido.items} - <span className="text-green-600">${pedido.total}</span>
              </li>
            ))}
          </ul>
        </div>

        {/* 🍔 Productos */}
        <div className="bg-white p-4 rounded shadow mb-6">
          <h2 className="text-xl font-bold mb-3">🍕 Productos</h2>
          <ul>
            {productos.map((producto) => (
              <li key={producto.id} className="border p-2 my-2">
                {producto.nombre} - {producto.categoria} - <span className="text-blue-600">${producto.precio.toFixed(2)}</span>
              </li>
            ))}
          </ul>
        </div>

        {/* ➕ Agregar Producto */}
        <div className="bg-white p-4 rounded shadow">
          <h2 className="text-xl font-bold mb-3">➕ Agregar Producto</h2>
          <form onSubmit={handleAgregarProducto} className="flex flex-col space-y-4">
            <input
              type="text"
              placeholder="Nombre del Producto"
              value={nuevoProducto.nombre}
              onChange={(e) => setNuevoProducto({ ...nuevoProducto, nombre: e.target.value })}
              className="border p-2 rounded"
              required
            />
            <select
              value={nuevoProducto.categoria}
              onChange={(e) => setNuevoProducto({ ...nuevoProducto, categoria: e.target.value })}
              className="border p-2 rounded"
              required
            >
              <option value="">Selecciona una categoría</option>
              <option value="bebida">Bebida</option>
              <option value="calzone">Calzone</option>
              <option value="pizza">Pizza</option>
              <option value="entrada">Entrada</option>
              <option value="postre">Postre</option>
              <option value="pasta">Pasta</option>
              <option value="postre">Postre</option>
              <option value="brochetas">Brochetas</option>
              <option value="batidos">Batidos</option>
              <option value="asados">Asados</option>
              <option value="extra">Extra</option>
            </select>
            <input
              type="text"
              placeholder="Precio"
              value={nuevoProducto.precio}
              onChange={(e) => setNuevoProducto({ ...nuevoProducto, precio: e.target.value })}
              className="border p-2 rounded"
              required
            />
            <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
              Agregar Producto
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Admin;
