import { useState } from "react";
import Navbar from "../components/Navbar";

const Mesas = () => {
  const [selectedMesa, setSelectedMesa] = useState(null);
  const [pedido, setPedido] = useState({
    categoria: "",
    tipo: "",
    tamaño: "",
    nombre: "",
  });

  const mesas = [1, 2, 3, 4, 5]; // Números de las mesas

  const handleSeleccionarMesa = (mesa) => {
    setSelectedMesa(mesa);
  };

  const handleSubmitPedido = (e) => {
    e.preventDefault();
    // Aquí puedes agregar la lógica para enviar el pedido al backend
    alert(`Pedido para Mesa ${selectedMesa}: ${pedido.categoria}, ${pedido.tipo}, ${pedido.tamaño}, ${pedido.nombre}`);
  };

  return (
    <div>
      <Navbar />
      <div className="p-5">
        <h1 className="text-3xl font-bold mb-4">Mesas Disponibles</h1>
        <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-5 gap-4">
          {mesas.map((mesa) => (
            <div
              key={mesa}
              className="border p-4 rounded shadow flex flex-col items-center bg-white"
            >
              <h2 className="text-xl font-semibold">Mesa {mesa}</h2>
              <button
                className="mt-2 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
                onClick={() => handleSeleccionarMesa(mesa)}
              >
                Seleccionar Mesa
              </button>
            </div>
          ))}
        </div>

        {selectedMesa && (
          <div className="mt-8 p-4 border rounded shadow bg-white">
            <h2 className="text-xl font-semibold mb-4">Agregar Pedido para Mesa {selectedMesa}</h2>
            <form onSubmit={handleSubmitPedido}>
              <div className="mb-4">
                <label className="block mb-2">Categoría:</label>
                <select
                  className="border p-2 w-full"
                  value={pedido.categoria}
                  onChange={(e) => setPedido({ ...pedido, categoria: e.target.value })}
                  required
                >
                  <option value="">Selecciona una categoría</option>
                  <option value="pizza">Pizza</option>
                  <option value="calzone">Calzone</option>
                  <option value="entrada">Entrada</option>
                  <option value="postre">Postre</option>
                  <option value="bebida">Bebida</option>
                </select>
              </div>

              {pedido.categoria === "pizza" && (
                <div className="mb-4">
                  <label className="block mb-2">Tipo de Pizza:</label>
                  <select
                    className="border p-2 w-full"
                    value={pedido.tipo}
                    onChange={(e) => setPedido({ ...pedido, tipo: e.target.value })}
                    required
                  >
                    <option value="">Selecciona un tipo</option>
                    <option value="Margarita">Margarita</option>
                    <option value="4 Estaciones">4 Estaciones</option>
                    <option value="Abbruzzese">Abbruzzese</option>
                    <option value="La Bambina">La Bambina</option>
                    <option value="Vegetariana">Vegetariana</option>
                    <option value="De la Nona - Especial">De la Nona - Especial</option>
                    <option value="Americana">Americana</option>
                    <option value="Hawaiana">Hawaiana</option>
                    <option value="Combinación">Combinación</option>
                    <option value="Frutos del Mar">Frutos del Mar</option>
                  </select>
                </div>
              )}

              {pedido.categoria === "pizza" && (
                <div className="mb-4">
                  <label className="block mb-2">Tamaño de Pizza:</label>
                  <select
                    className="border p-2 w-full"
                    value={pedido.tamaño}
                    onChange={(e) => setPedido({ ...pedido, tamaño: e.target.value })}
                    required
                  >
                    <option value="">Selecciona un tamaño</option>
                    <option value="Regular">Regular</option>
                    <option value="Familiar">Familiar</option>
                    <option value="1/2 Metro">1/2 Metro</option>
                  </select>
                </div>
              )}

              {pedido.categoria === "calzone" && (
                <div className="mb-4">
                  <label className="block mb-2">Tipo de Calzone:</label>
