import { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "../components/Navbar";

const Mesas = () => {
  const [mesas, setMesas] = useState([]);
  const [selectedMesa, setSelectedMesa] = useState(null);
  const [categoria, setCategoria] = useState("");

  useEffect(() => {
    const fetchMesas = async () => {
      const res = await axios.get("http://localhost:8000/mesas/", {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setMesas(res.data);
    };
    fetchMesas();
  }, []);

  const handlePedido = async () => {
    if (!selectedMesa || !categoria) return;

    try {
      const res = await axios.post(
        "http://localhost:8000/pedidos/",
        { mesa_id: selectedMesa, total: 0, estado: "pendiente", categoria: categoria },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );
      alert("Pedido realizado con éxito");
    } catch (error) {
      console.error("Error al realizar el pedido:", error);
      alert("Error al realizar el pedido");
    }
  };

  return (
    <div>
      <Navbar />
      <div className="p-5">
        <h1 className="text-3xl font-bold mb-4">Mesas Disponibles</h1>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {mesas.map((mesa) => (
            <div
              key={mesa.id}
              className="border p-4 rounded shadow flex flex-col items-center bg-white"
            >
              <h2 className="text-xl font-semibold">Mesa {mesa.numero}</h2>
              <p className="text-gray-600">
                {mesa.ocupada ? "Ocupada" : "Libre"}
              </p>
              {!mesa.ocupada && (
                <button
                  className="mt-2 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
                  onClick={() => setSelectedMesa(mesa.id)}
                >
                  Seleccionar Mesa
                </button>
              )}
            </div>
          ))}
        </div>

        {selectedMesa && (
          <div className="mt-4">
            <h2 className="text-xl font-semibold mb-2">Realizar Pedido</h2>
            <select
              className="border p-2 mb-2"
              value={categoria}
              onChange={(e) => setCategoria(e.target.value)}
            >
              <option value="">Selecciona una categoría</option>
              <option value="bebida">Bebida</option>
              <option value="pizza">Pizza</option>
            </select>
            <button
              className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
              onClick={handlePedido}
            >
              Realizar Pedido
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Mesas;
