import { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "../components/Navbar"; // Asegúrate de importar el Navbar desde la ubicación correcta

const Mesas = () => {
  const [mesas, setMesas] = useState([]);

  useEffect(() => {
    const fetchMesas = async () => {
      try {
        const res = await axios.get("http://localhost:8000/mesas/", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        console.log("Datos recibidos del backend:", res.data); // Agrega este log
        setMesas(res.data);
      } catch (error) {
        console.error("Error al obtener las mesas:", error);
      }
    };
    fetchMesas();
  }, []);

  const handlePedido = async (mesaId) => {
    try {
      const res = await axios.post(
        "http://localhost:8000/pedidos/",
        { mesa_id: mesaId, total: 0, estado: "pendiente" },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );
      alert("Pedido realizado con éxito");
    } catch (error) {
      console.error("Error al realizar el pedido:", error);
      alert("Error al realizar el pedido");
    }
  };

  return (
    <div>
      <Navbar /> {/* Incluye el Navbar aquí */}
      <div className="p-5">
        <h1 className="text-3xl font-bold mb-4">Mesas Disponibles</h1>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {mesas.length > 0 ? (
            mesas.map((mesa) => (
              <div
                key={mesa.id}
                className="border p-4 rounded shadow flex flex-col items-center justify-between bg-white"
              >
                <h2 className="text-xl font-semibold">Mesa {mesa.numero}</h2>
                <p className="text-gray-600">
                  {mesa.ocupada ? "Ocupada" : "Libre"}
                </p>
                {!mesa.ocupada && (
                  <button
                    className="mt-2 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
                    onClick={() => handlePedido(mesa.id)}
                  >
                    Realizar Pedido
                  </button>
                )}
              </div>
            ))
          ) : (
            <p>No hay mesas disponibles.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Mesas;
