import { useState, useEffect } from "react";
import axios from "axios";

const Mesas = () => {
  const [mesas, setMesas] = useState([]);

  useEffect(() => {
    const fetchMesas = async () => {
      const res = await axios.get("http://localhost:8000/mesas/", {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setMesas(res.data);
    };
    fetchMesas();
  }, []);

  return (
    <div className="p-5">
      <h1 className="text-2xl">Mesas</h1>
      <ul>
        {mesas.map((mesa) => (
          <li key={mesa.id} className="border p-2 my-2">
            Mesa {mesa.numero} - {mesa.ocupada ? "Ocupada" : "Libre"}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Mesas;
