import { useEffect, useState } from "react";
import Navbar from "../components/Navbar"; // AsegÃºrate de importar el Navbar

const Admin = () => {
  const [pedidos, setPedidos] = useState([]);
  const [notificaciones, setNotificaciones] = useState([]);

  useEffect(() => {
    const ws = new WebSocket("ws://localhost:8000/ws/admin");

    ws.onmessage = (event) => {
      setNotificaciones((prev) => [...prev, event.data]);
    };

    return () => ws.close();
  }, []);

  return (
    <div>
      <Navbar /> {/* Incluye el Navbar aquÃ­ */}
      <div className="p-5">
        <h1 className="text-3xl font-bold mb-4">Panel de AdministraciÃ³n</h1>

        <div className="flex flex-col md:flex-row space-y-4 md:space-y-0 md:space-x-4">
          {/* SecciÃ³n de Notificaciones */}
          <div className="w-full md:w-1/2 bg-white p-4 rounded shadow">
            <h2 className="text-xl font-semibold mb-2">ðŸ”” Notificaciones</h2>
            <ul>
              {notificaciones.map((msg, index) => (
                <li key={index} className="border-b p-2">{msg}</li>
              ))}
            </ul>
          </div>

          {/* SecciÃ³n de Pedidos */}
          <div className="w-full md:w-1/2 bg-white p-4 rounded shadow">
            <h2 className="text-xl font-semibold mb-2">ðŸ“Œ Pedidos</h2>
            <ul>
              {pedidos.map((pedido) => (
                <li key={pedido.id} className="border-b p-2">
                  Pedido en Mesa {pedido.mesa_id} - Estado: {pedido.estado} - Total: ${pedido.total}
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Admin;
