import { useState } from "react";
import Navbar from "../components/Navbar";

const Mesas = () => {
  const [selectedMesa, setSelectedMesa] = useState(null);
  const [pedido, setPedido] = useState({ tipo: "", tamaño: "", nombre: "" });

  const mesas = [1, 2, 3, 4, 5]; // Números de las mesas

  const handleSeleccionarMesa = (mesa) => {
    setSelectedMesa(mesa);
  };

  const handleSubmitPedido = (e) => {
    e.preventDefault();
    // Aquí puedes agregar la lógica para enviar el pedido al backend
    alert(`Pedido para Mesa ${selectedMesa}: ${pedido.tipo}, ${pedido.tamaño}, ${pedido.nombre}`);
  };

  return (
    <div>
      <Navbar />
      <div className="p-5">
        <h1 className="text-3xl font-bold mb-4">Mesas Disponibles</h1>
        <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-5 gap-4">
          {mesas.map((mesa) => (
            <div
              key={mesa}
              className="border p-4 rounded shadow flex flex-col items-center bg-white"
            >
              <h2 className="text-xl font-semibold">Mesa {mesa}</h2>
              <button
                className="mt-2 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
                onClick={() => handleSeleccionarMesa(mesa)}
              >
                Seleccionar Mesa
              </button>
            </div>
          ))}
        </div>

        {selectedMesa && (
          <div className="mt-8 p-4 border rounded shadow bg-white">
            <h2 className="text-xl font-semibold mb-4">Agregar Pedido para Mesa {selectedMesa}</h2>
            <form onSubmit={handleSubmitPedido}>
              <div className="mb-4">
                <label className="block mb-2">Tipo de Pedido:</label>
                <select
                  className="border p-2 w-full"
                  value={pedido.tipo}
                  onChange={(e) => setPedido({ ...pedido, tipo: e.target.value })}
                  required
                >
                  <option value="">Selecciona un tipo</option>
                  <option value="bebida">Bebida</option>
                  <option value="pizza">Pizza</option>
                </select>
              </div>

              {pedido.tipo === "bebida" && (
                <div className="mb-4">
                  <label className="block mb-2">Tamaño de Bebida:</label>
                  <select
                    className="border p-2 w-full"
                    value={pedido.tamaño}
                    onChange={(e) => setPedido({ ...pedido, tamaño: e.target.value })}
                    required
                  >
                    <option value="">Selecciona un tamaño</option>
                    <option value="lata">Lata</option>
                    <option value="litro">Litro</option>
                    <option value="medio litro">Medio Litro</option>
                  </select>
                </div>
              )}

              {pedido.tipo === "pizza" && (
                <div className="mb-4">
                  <label className="block mb-2">Tamaño de Pizza:</label>
                  <select
                    className="border p-2 w-full"
                    value={pedido.tamaño}
                    onChange={(e) => setPedido({ ...pedido, tamaño: e.target.value })}
                    required
                  >
                    <option value="">Selecciona un tamaño</option>
                    <option value="8 pulgadas">8 Pulgadas</option>
                    <option value="12 pulgadas">12 Pulgadas</option>
                  </select>
                </div>
              )}

              <div className="mb-4">
                <label className="block mb-2">Nombre del Producto:</label>
                <input
                  type="text"
                  className="border p-2 w-full"
                  value={pedido.nombre}
                  onChange={(e) => setPedido({ ...pedido, nombre: e.target.value })}
                  required
                />
              </div>

              <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600">
                Agregar Pedido
              </button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default Mesas;
